import React, {Component} from 'react';
import axios from 'axios';
let ref;
export default class LIST_PRODUCT extends Component {

    constructor(props) {
        super(props);
        ref=this;
        ref.state = {
            username:ref.props.location.username,
            products: [],
            all_products:[]
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/a1')
             .then(response => {
                ref.setState({products: response.data});
                axios.get('http://localhost:4000/a')
                    .then(response => {
                    ref.setState({all_products: response.data});
                })
                    .catch(function(error) {
                    console.log(error);
             })

             })
             .catch(function(error) {
                 console.log(error);
             })

    }

    render() {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Vendorname</th>
                            <th>Productname</th>
                            <th>Quantity</th>
                            <th>Status</th>
                            <th>Customername</th> 

                        </tr>
                    </thead>
                    <tbody>
                    { 
                        ref.state.all_products.map((currentProduct, i) => {
                            // ref.state.all_products.map((it,i) =>{
                            // if(currentProduct.customername ===   ref.state.username  && currentProduct.vendorname === it.username && currentProduct.productname === it.productname)
                            return (
                                <tr>
                                    <td>{currentProduct.vendorname}</td>
                                    <td>{currentProduct.productname}</td>
                                    <td>{currentProduct.quantity}</td>
                                    <td>{currentProduct.status}</td>
                                    <td>{currentProduct.customername}</td>
                                </tr>
                            )
                            // })
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
