import React, {Component} from 'react';
import axios from 'axios';
let ref;
export default class Searched_Products extends Component {

    constructor(props) {
        // alert("debuff");
        super(props);
        ref=this;       
        ref.state = {
            username:ref.props.location.username,
            productname :ref.props.location.product_name,
            // productname:'',
            products: []
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/a')
             .then(response => {
                 ref.setState({products: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }
    Order=(ownername) =>{
        // this.props.history.push()
        // alert("you are ordering");
        // alert(this.state.username);
        this.props.history.push({
            pathname:'/login/customer/search-products/products/order',
            customername:this.state.username,
            vendorname:ownername,
            productname:this.state.productname
          });
    }

    sortByPrice=()=>{

        let sortedProductsAsc;
        sortedProductsAsc= this.state.products.sort((a,b)=>{
           return parseInt(a.price)  - parseInt(b.price);
        })

        this.setState({
            products:sortedProductsAsc
        })
    }
    sortByQuantity=()=>{

        let sortedProductsAsc;
        sortedProductsAsc= this.state.products.sort((a,b)=>{
           return parseInt(a.quantity-a.count)  - parseInt(b.quantity-b.count);
        })

        this.setState({
            products:sortedProductsAsc
        })
    }
    sortByRating=()=>{

        let sortedProductsAsc;
        sortedProductsAsc= this.state.products.sort((a,b)=>{
           return parseInt(a.price)  - parseInt(b.price);
        })
        this.setState({
            products:sortedProductsAsc
        })
    }
    render() {

        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Username</th>
                            <th>Productname</th>
                            <th>Price</th>
                            <th>Quantity Remaining in the Bundle</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        ref.state.products.map((currentProduct, i) => {
                            if(currentProduct.productname === ref.state.productname && currentProduct.status === "Waiting")
                            return (
                                <tr>
                                    <td>{currentProduct.username}</td>
                                    <td>{currentProduct.productname}</td>
                                    <td>{currentProduct.price}</td>
                                    <td>{currentProduct.quantity - currentProduct.count}</td>  
                                    <button type="button" onClick={() => this.Order(currentProduct.username)}>ORDER</button>
                                    <button type="button" onClick={() => this.Edit(currentProduct.vendorname,currentProduct.productname,currentProduct.quantity)}>Edit</button>
                                    <button type="button" onClick={() => this.sortByPrice(currentProduct.vendorname,currentProduct.productname,currentProduct.quantity)}>SORT BY PRICE</button>
                                    <button type="button" onClick={() => this.sortByQuantity(currentProduct.vendorname,currentProduct.productname,currentProduct.quantity)}>SORT BY QUANTITY OF ITEMS IN BUNDLE</button>
                                    <button type="button" onClick={() => this.sortByRating(currentProduct.vendorname,currentProduct.productname,currentProduct.quantity)}>SORT BY RATING OF SELLER</button>

                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
